import React from 'react';
import './Hero.css';

const Hero = () => {
  return (
    <section className="hero" id="home">
      <div className="hero-container">
        <div className="hero-content">
          <h1>Chào mừng đến với TechKids</h1> {/* Tiêu đề chính của trang */}
          <p>
            Nơi bắt đầu hành trình khám phá thế giới công nghệ cho thế hệ trẻ Việt Nam. 
            Chúng tôi cung cấp các khóa học chất lượng cao về lập trình và kỹ năng công nghệ.
          </p> {/* Đoạn mô tả ngắn về TechKids */}
          
          <div className="hero-buttons">
            <a href="#courses" className="btn btn-primary">Khám phá khóa học</a> {/* Nút CTA chính */}
            <a href="#contact" className="btn btn-secondary"n>Liên hệ tư vấn</a> {/* Nút CTA phụ */}
          </div>
        </div>
        
        <div className="hero-image">
          {/* Sử dụng hình ảnh phù hợp với chủ đề công nghệ/giáo dục */}
          <img src="/images/hero-image.svg" alt="TechKids Education" />
        </div>
      </div>
      
      <div className="hero-wave">
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1440 320">
          <path fill="#ffffff" fillOpacity="1" d="M0,96L48,112C96,128,192,160,288,160C384,160,480,128,576,117.3C672,107,768,117,864,138.7C960,160,1056,192,1152,192C1248,192,1344,160,1392,144L1440,128L1440,320L1392,320C1344,320,1248,320,1152,320C1056,320,960,320,864,320C768,320,672,320,576,320C480,320,384,320,288,320C192,320,96,320,48,320L0,320Z"></path>
        </svg> {/* SVG tạo hiệu ứng sóng ở cuối phần hero */}
      </div>
    </section>
  );
};

export default Hero;
