import React from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import './App.css';
import Navbar from './components/Navbar/Navbar';
import Hero from './components/Hero/Hero';
import StudentSection from './components/StudentSection/StudentSection';
import Schedule from './components/Schedule/Schedule';
import Contact from './components/Contact/Contact';
import AllCourses from './components/ /AllCourses';
import Footer from './components/Footer/Footer';

// Component cho trang chủ
const Home = () => {
  const navigate = useNavigate(); // Hook để điều hướng

  return (
    <>
      <Hero />
      <StudentSection />
      <section className="schedule">
        <h2>Lịch Học</h2>
        <div className="schedule-content">
          <p>Các lớp học được tổ chức linh hoạt, phù hợp với thời gian của học viên</p>
          <button className="schedule-button" onClick={() => navigate('/schedule')}>
            Xem lịch học
          </button>
        </div>
      </section>
      <div className="courses-section">
        <button 
          className="view-all-studentsection" 
          onClick={() => navigate('/StudentSection')}
        >
          Xem tất cả khóa học
        </button>
      </div>
    </>
  );
};

function App() {
  return (
    <Router>
      <div className="app">
        <Navbar />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/schedule" element={<Schedule />} />
          <Route path="/contact" element={<Contact />} />
          <Route path="/courses" element={<AllCourses />} />
        </Routes>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
