import React, { useState } from 'react';
import './Contact.css';

const Contact = () => {
  // State để lưu trữ dữ liệu form
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    subject: '',
    message: ''
  });
  
  // State để hiển thị thông báo sau khi gửi form
  const [formStatus, setFormStatus] = useState({
    submitted: false,
    success: false,
    message: ''
  });

  // Hàm xử lý thay đổi input
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  // Hàm xử lý submit form
  const handleSubmit = (e) => {
    e.preventDefault();
    
    // Kiểm tra xem form đã điền đầy đủ thông tin chưa
    if (!formData.name || !formData.email || !formData.message) {
      setFormStatus({
        submitted: true,
        success: false,
        message: 'Vui lòng điền đầy đủ thông tin bắt buộc.'
      });
      return;
    }
    
    // Mô phỏng việc gửi form đến server
    // Trong thực tế, đây sẽ là API call
    setTimeout(() => {
      setFormStatus({
        submitted: true,
        success: true,
        message: 'Cảm ơn bạn đã gửi thông tin. Chúng tôi sẽ liên hệ lại sớm nhất có thể!'
      });
      
      // Reset form data
      setFormData({
        name: '',
        email: '',
        phone: '',
        subject: '',
        message: ''
      });
      
      // Sau 5 giây, reset trạng thái form
      setTimeout(() => {
        setFormStatus({
          submitted: false,
          success: false,
          message: ''
        });
      }, 5000);
    }, 1000);
  };

  return (
    <section className="contact-section section" id="contact">
      <div className="container">
        <h2 className="section-title">Liên Hệ Với Chúng Tôi</h2> {/* Tiêu đề phần */}
        
        <div className="contact-container">
          <div className="contact-info">
            <div className="info-item">
              <div className="info-icon">
                <i className="fas fa-map-marker-alt"></i>
              </div>
              <div className="info-content">
                <h3>Địa chỉ</h3>
                <p>Ở đâu nào có tên miền rồi cho biết</p>
              </div>
            </div>
            
            <div className="info-item">
              <div className="info-icon">
                <i className="fas fa-phone-alt"></i>
              </div>
              <div className="info-content">
                <h3>Điện thoại</h3>
                <p>+84 399 760 075</p>
              </div>
            </div>
            
            <div className="info-item">
              <div className="info-icon">
                <i className="fas fa-envelope"></i>
              </div>
              <div className="info-content">
                <h3>Email</h3>
                <p>xuantruong@techkids.edu.vn</p>
              </div>
            </div>
            
            <div className="info-item">
              <div className="info-icon">
                <i className="fas fa-clock"></i>
              </div>
              <div className="info-content">
                <h3>Giờ làm việc</h3>
                <p>Thứ 2 - Thứ 6: 8:00 - 17:30</p>
                <p>Thứ 7: 8:00 - 12:00</p>
              </div>
            </div>
          </div>
          
          <div className="contact-form-container">
            {formStatus.submitted && (
              <div className={`form-message ${formStatus.success ? 'success' : 'error'}`}>
                {formStatus.message}
              </div>
            )}
            
            <form className="contact-form" onSubmit={handleSubmit}>
              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="name">Họ và tên <span className="required">*</span></label>
                  <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    placeholder="Nhập họ và tên của bạn"
                  />
                </div>
                
                <div className="form-group">
                  <label htmlFor="email">Email <span className="required">*</span></label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    placeholder="Nhập địa chỉ email của bạn"
                  />
                </div>
              </div>
              
              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="phone">Số điện thoại</label>
                  <input
                    type="tel"
                    id="phone"
                    name="phone"
                    value={formData.phone}
                    onChange={handleChange}
                    placeholder="Nhập số điện thoại của bạn"
                  />
                </div>
                
                <div className="form-group">
                  <label htmlFor="subject">Chủ đề</label>
                  <input
                    type="text"
                    id="subject"
                    name="subject"
                    value={formData.subject}
                    onChange={handleChange}
                    placeholder="Nhập chủ đề liên hệ"
                  />
                </div>
              </div>
              
              <div className="form-group">
                <label htmlFor="message">Nội dung <span className="required">*</span></label>
                <textarea
                  id="message"
                  name="message"
                  value={formData.message}
                  onChange={handleChange}
                  rows="5"
                  placeholder="Nhập nội dung tin nhắn của bạn"
                ></textarea>
              </div>
              
              <button type="submit" className="submit-button">Gửi tin nhắn</button> {/* Nút submit form */}
            </form>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Contact;
