import React, { useState, useEffect } from 'react';
import './ParentTestimonials.css';

/**
 * ParentTestimonials Component - Hiển thị đánh giá của phụ huynh
 * 
 * Chức năng:
 * - Hiển thị slider các testimonials từ phụ huynh
 * - Tự động chuyển slide sau mỗi 5 giây
 * - Có thể điều khiển bằng nút prev/next
 * - Hiển thị dots navigation
 */

const ParentTestimonials = () => {
  // Data mẫu cho testimonials
  const testimonials = [
    {
      id: 1,
      name: "Nguyễn Thị Hương",
      role: "Phụ huynh học sinh lớp Scratch Jr",
      content: "Con tôi rất thích thú với các bài học tại TechKids. Các thầy cô nhiệt tình, tận tâm và phương pháp giảng dạy rất phù hợp với trẻ em.",
      image: "/assets/images/parent-1.jpg"
    },
    {
      id: 2,
      name: "Trần Văn Minh",
      role: "Phụ huynh học sinh lớp Python",
      content: "TechKids đã giúp con tôi phát triển không chỉ kỹ năng lập trình mà còn tư duy logic và khả năng giải quyết vấn đề.",
      image: "/assets/images/parent-2.jpg"
    },
    {
      id: 3,
      name: "Lê Thu Trang",
      role: "Phụ huynh học sinh lớp Web Design",
      content: "Môi trường học tập năng động, sáng tạo và an toàn. Con tôi đã có thể tự tạo được website của riêng mình sau khóa học.",
      image: "/assets/images/parent-3.jpg"
    }
  ];

  // State quản lý slide hiện tại
  const [currentSlide, setCurrentSlide] = useState(0);

  // Hàm chuyển đến slide tiếp theo
  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % testimonials.length);
  };

  // Hàm chuyển đến slide trước
  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + testimonials.length) % testimonials.length);
  };

  // Hàm chuyển đến slide cụ thể
  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  // Effect tự động chuyển slide
  useEffect(() => {
    const timer = setInterval(nextSlide, 5000);
    return () => clearInterval(timer);
  }, []);

  return (
    <section className="testimonials-section">
      <div className="container">
        {/* Tiêu đề section */}
        <h2 className="section-title">Phụ Huynh Nói Gì Về TechKids?</h2>
        
        {/* Slider container */}
        <div className="testimonials-slider">
          {/* Nút Previous */}
          <button className="slider-btn prev" onClick={prevSlide}>
            <i className="fas fa-chevron-left"></i>
          </button>

          {/* Testimonial cards */}
          <div className="testimonials-container">
            {testimonials.map((testimonial, index) => (
              <div
                key={testimonial.id}
                className={`testimonial-card ${index === currentSlide ? 'active' : ''}`}
              >
                <div className="testimonial-image">
                  <img src={testimonial.image} alt={testimonial.name} />
                </div>
                <div className="testimonial-content">
                  <p className="testimonial-text">{testimonial.content}</p>
                  <h3 className="testimonial-name">{testimonial.name}</h3>
                  <p className="testimonial-role">{testimonial.role}</p>
                </div>
              </div>
            ))}
          </div>

          {/* Nút Next */}
          <button className="slider-btn next" onClick={nextSlide}>
            <i className="fas fa-chevron-right"></i>
          </button>
        </div>

        {/* Dots navigation */}
        <div className="slider-dots">
          {testimonials.map((_, index) => (
            <button
              key={index}
              className={`dot ${index === currentSlide ? 'active' : ''}`}
              onClick={() => goToSlide(index)}
            ></button>
          ))}
        </div>
      </div>
    </section>
  );
};

export default ParentTestimonials;
