// Import các thư viện cần thiết
import React, { useState } from 'react';
import './Contact.css';

// Khai báo dữ liệu tĩnh cho thông tin liên hệ
const CONTACT_INFO = [
  {
    icon: 'map-marker-alt',    // Icon địa chỉ từ Font Awesome
    title: 'Địa Chỉ',         // Tiêu đề
    content: 'Ở đâu còn lâu mới nói'  // Nội dung
  },
  // Tương tự cho các thông tin khác...
];

// Khai báo dữ liệu tĩnh cho các liên kết mạng xã hội
const SOCIAL_LINKS = [
  {
    icon: 'facebook-f',   // Icon Facebook
    url: 'https://facebook.com/techkids',  // Link Facebook
    class: 'facebook'     // Class CSS cho style
  },
  // Tương tự cho các mạng xã hội khác...
];

// Component chính
const Contact = () => {
  // Khởi tạo state ban đầu cho form
  const initialFormState = {
    fullName: '',    // Tên người dùng
    email: '',       // Email
    phone: '',       // Số điện thoại
    message: ''      // Nội dung tin nhắn
  };

  // State quản lý dữ liệu form
  const [formData, setFormData] = useState(initialFormState);
  
  // State quản lý thông báo
  const [notification, setNotification] = useState({
    show: false,     // Hiển thị/ẩn thông báo
    type: '',        // Loại thông báo (success/error)
    message: ''      // Nội dung thông báo
  });

  // Xử lý khi người dùng nhập liệu
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,           // Giữ nguyên các giá trị cũ
      [name]: value      // Cập nhật giá trị mới
    }));
  };

  // Xử lý khi submit form
  const handleSubmit = (e) => {
    e.preventDefault();  // Ngăn form reload trang

    // Kiểm tra các trường bắt buộc
    const requiredFields = ['fullName', 'email', 'message'];
    const isValid = requiredFields.every(field => formData[field].trim());

    if (!isValid) {
      showNotification('error', 'Vui lòng điền đầy đủ thông tin bắt buộc');
      return;
    }

    // Xử lý thành công
    showNotification('success', 'Gửi thông tin thành công!');
    setFormData(initialFormState);  // Reset form
  };

  // Hàm hiển thị thông báo
  const showNotification = (type, message) => {
    setNotification({ show: true, type, message });
    // Tự động ẩn thông báo sau 3 giây
    setTimeout(() => {
      setNotification({ show: false, type: '', message: '' });
    }, 3000);
  };

  // Component con hiển thị thông tin liên hệ
  const InfoItem = ({ icon, title, content }) => (
    <div className="info-item">
      <div className="icon-circle">
        <i className={`fas fa-${icon}`}></i>
      </div>
      <div className="info-content">
        <h4>{title}</h4>
        <p>{content}</p>
      </div>
    </div>
  );

  // Component con cho input field
  const InputField = ({ type = 'text', name, label, required = false, ...props }) => (
    <div className="input-group">
      <input
        type={type}
        name={name}
        value={formData[name]}
        onChange={handleChange}
        required={required}
        {...props}
      />
      <label>{label}{required && ' *'}</label>
      <div className="input-line"></div>
    </div>
  );

  // Render component
  return (
    <div className="contact-section">
      {/* Phần header */}
      <div className="contact-header">...</div>

      <div className="contact-container">
        {/* Phần thông tin liên hệ */}
        <div className="contact-info-section">...</div>

        {/* Phần form liên hệ */}
        <div className="contact-form-section">...</div>
      </div>
    </div>
  );
};
