import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';
import Navbar from './components/Navbar/Navbar';
import Hero from './components/Hero/Hero';
import StudentSection from './components/StudentSection/StudentSection';
import Schedule from './components/Schedule/Schedule';
import Contact from './components/Contact/Contact';
import Footer from './components/Footer/Footer';

function App() {
  return (
    <Router>
      <div className="app">
        <Navbar />
        
        <Routes>
          {/* Trang chủ */}
          <Route path="/" element={
            <>
              <Hero />
              <StudentSection />
              <Schedule />
              <Contact />
            </>
          } />
          
          {/* Trang Khóa học */}
          <Route path="/khoa-hoc" element={<StudentSection />} />
          
          {/* Trang Lịch học */}
          <Route path="/lich-hoc" element={<Schedule />} />
          
          {/* Trang Liên hệ */}
          <Route path="/lien-he" element={<Contact />} />
        </Routes>

        <Footer />
      </div>
    </Router>
  );
}

export default App;

/*
Giải thích cấu trúc:

1. Định tuyến:
   - BrowserRouter bọc toàn bộ ứng dụng để quản lý điều hướng
   - Routes định nghĩa các đường dẫn có thể truy cập
   - Route xác định nội dung hiển thị cho từng đường dẫn

2. Bố cục:
   - Thanh điều hướng và Chân trang xuất hiện ở tất cả các trang
   - Nội dung thay đổi theo đường dẫn được chọn

3. Các thành phần:
   - Tái sử dụng các thành phần cho nhiều trang khác nhau
   - Dễ dàng thêm mới các trang khi cần

4. Trải nghiệm người dùng:
   - Chuyển trang mượt mà không cần tải lại
   - Giữ nguyên trạng thái các thành phần
   - Tương tác nhanh và hiệu quả
*/
