import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // Import Link từ react-router-dom
import './Navbar.css';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const navigate = useNavigate();

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        {/* Logo sẽ link về trang chủ */}
        <Link to="/" className="navbar-logo">
          TechKids
        </Link>

        {/* Nút toggle cho mobile menu */}
        <div className="menu-icon" onClick={toggleMenu}>
          <i className={isOpen ? 'fas fa-times' : 'fas fa-bars'} />
        </div>

        {/* Menu items */}
        <ul className={isOpen ? 'nav-menu active' : 'nav-menu'}>
          <li className="nav-item">
            <Link to="/" className="nav-link" onClick={() => setIsOpen(false)}>
              Trang chủ
            </Link>
          </li>
          
          <li className="nav-item">
            <Link to="/schedule" className="nav-link" onClick={() => setIsOpen(false)}>
              Lịch học
            </Link>
          </li>
          
          <li className="nav-item">
            <Link to="/contact" className="nav-link" onClick={() => setIsOpen(false)}>
              Liên hệ
            </Link>
          </li>
          
          {/* Nút đăng ký */}
          <li className="nav-item">
            <button 
              className="nav-button"
              onClick={() => {
                setIsOpen(false);
                navigate('/contact'); // Điều hướng đến trang liên hệ khi click đăng ký
              }}
            >
              Đăng ký ngay
            </button>
          </li>
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
