import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';
import Navbar from '/components/Navbar';
import Hero from './components/Hero';
import StudentSection from './components/StudentSection';
import Schedule from './components/Schedule';
import Contact from './components/Contact';
import Footer from './components/Footer';

function App() {
  return (
    <Router>
      <div className="app">
        <Navbar />
        
        <Routes>
          {/* Trang chủ */}
          <Route path="/" element={
            <>
              <Hero />
              <StudentSection />
              <Schedule />
              <Contact />
            </>
          } />
          
          {/* Trang Khóa học */}
          <Route path="/khoa-hoc" element={<StudentSection />} />
          
          {/* Trang Lịch học */}
          <Route path="/lich-hoc" element={<Schedule />} />
          
          {/* Trang Liên hệ */}
          <Route path="/lien-he" element={<Contact />} />
        </Routes>

        <Footer />
      </div>
    </Router>
  );
}

export default App;

/*
Giải thích cấu trúc:

1. Routing:
   - BrowserRouter bao bọc toàn bộ ứng dụng
   - Routes định nghĩa các đường dẫn
   - Route cho từng trang cụ thể

2. Layout:
   - Navbar và Footer xuất hiện ở tất cả các trang
   - Nội dung thay đổi theo route

3. Components:
   - Tái sử dụng components cho các trang khác nhau
   - Có thể thêm các trang mới dễ dàng

4. UX:
   - Navigation mượt mà giữa các trang
   - Không reload trang khi chuyển route
   - Giữ trạng thái của components
*/
