import React, { useState, useEffect } from 'react';
import './Navbar.css';

const Navbar = () => {
  const [scrolled, setScrolled] = useState(false);
  
  // Hook useEffect để theo dõi sự kiện cuộn trang
  useEffect(() => {
    const handleScroll = () => {
      // Thay đổi trạng thái thanh navbar khi cuộn xuống quá 50px
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };
    
    window.addEventListener('scroll', handleScroll);
    
    // Cleanup function để tránh memory leak
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);
  
  return (
    <nav className={`navbar ${scrolled ? 'scrolled' : ''}`}>
      <div className="navbar-container">
        <div className="navbar-logo">
          <img src="/logo.png" alt="TechKids Logo" />
          <span>TechKids</span>
        </div>
        
        <ul className="navbar-menu">
          <li className="navbar-item active">
            <a href="#home">Trang chủ</a> {/* Liên kết đến trang chủ */}
          </li>
          <li className="navbar-item">
            <a href="#courses">Khóa học</a> {/* Liên kết đến phần khóa học */}
          </li>
          <li className="navbar-item">
            <a href="#schedule">Lịch học</a> {/* Liên kết đến phần lịch học */}
          </li>
          <li className="navbar-item">
            <a href="#contact">Liên hệ</a> {/* Liên kết đến phần liên hệ */}
          </li>
        </ul>
        
        <button className="login-button">Đăng nhập</button> {/* Nút đăng nhập */}
      </div>
    </nav>
  );
};

export default Navbar;
