// Import các thư viện và hooks cần thiết
import React, { useState } from 'react';
import './Contact.css';

/**
 * Contact Component - Hiển thị form liên hệ và thông tin liên lạc
 * @returns {JSX.Element} Form liên hệ và thông tin
 */
const Contact = () => {
  // Khởi tạo state cho form data
  const [formData, setFormData] = useState({
    fullName: '',    // Lưu trữ tên người dùng
    email: '',       // Lưu trữ email
    phone: '',       // Lưu trữ số điện thoại
    message: ''      // Lưu trữ nội dung tin nhắn
  });

  // State quản lý thông báo
  const [notification, setNotification] = useState({
    show: false,     // Điều khiển hiển thị/ẩn thông báo
    type: '',        // Loại thông báo (success/error)
    message: ''      // Nội dung thông báo
  });

  // Xử lý khi người dùng nhập liệu
  const handleChange = (e) => {
    const { name, value } = e.target;  // Lấy tên trường và giá trị từ input
    setFormData(prevState => ({
      ...prevState,                    // Giữ nguyên các giá trị cũ
      [name]: value                    // Cập nhật giá trị mới cho trường tương ứng
    }));
  };

  // Xử lý khi submit form
  const handleSubmit = (e) => {
    e.preventDefault();  // Ngăn chặn form reload trang
    
    // Kiểm tra các trường bắt buộc
    if (!formData.fullName || !formData.email || !formData.message) {
      setNotification({
        show: true,
        type: 'error',
        message: 'Vui lòng điền đầy đủ thông tin bắt buộc'
      });
      return;
    }

    // Xử lý gửi form thành công
    setNotification({
      show: true,
      type: 'success',
      message: 'Cảm ơn bạn đã liên hệ. Chúng tôi sẽ phản hồi sớm nhất!'
    });

    // Reset form về trạng thái ban đầu
    setFormData({
      fullName: '',
      email: '',
      phone: '',
      message: ''
    });

    // Tự động ẩn thông báo sau 3 giây
    setTimeout(() => {
      setNotification({ show: false, type: '', message: '' });
    }, 3000);
  };

  return (
    <div className="contact-container">
      <div className="contact-header">
        <h1>Liên Hệ Với Chúng Tôi</h1>
        <p>Hãy để lại thông tin, chúng tôi sẽ phản hồi trong vòng 24 giờ</p>
      </div>

      <div className="contact-content">
        <div className="contact-info">
          <div className="info-card">
            <div className="info-item">
              <div className="info-icon">
                <i className="fas fa-map-marker-alt"></i>
              </div>
              <div className="info-text">
                <span>Địa chỉ</span>
                <p>Ở đâu còn lâu mới nói</p>
              </div>
            </div>

            <div className="info-item">
              <div className="info-icon">
                <i className="fas fa-phone-alt"></i>
              </div>
              <div className="info-text">
                <span>Điện thoại</span>
                <p>0399 760 075</p>
              </div>
            </div>

            <div className="info-item">
              <div className="info-icon">
                <i className="fas fa-envelope"></i>
              </div>
              <div className="info-text">
                <span>Email</span>
                <p>xuantruong@techkids.edu.vn</p>
              </div>
            </div>

            <div className="info-item">
              <div className="info-icon">
                <i className="fas fa-clock"></i>
              </div>
              <div className="info-text">
                <span>Giờ làm việc</span>
                <p>T2 - CN: 8:00 - 20:00</p>
              </div>
            </div>
          </div>

          <div className="social-links">
            <a href="https://facebook.com/techkids" target="_blank" rel="noopener noreferrer">
              <i className="fab fa-facebook-f"></i>
            </a>
            <a href="https://twitter.com/techkids" target="_blank" rel="noopener noreferrer">
              <i className="fab fa-twitter"></i>
            </a>
            <a href="https://instagram.com/techkids" target="_blank" rel="noopener noreferrer">
              <i className="fab fa-instagram"></i>
            </a>
            <a href="https://linkedin.com/company/techkids" target="_blank" rel="noopener noreferrer">
              <i className="fab fa-linkedin-in"></i>
            </a>
          </div>
        </div>

        <div className="contact-form-container">
          <form onSubmit={handleSubmit} className="contact-form">
            <div className="form-group">
              <input
                type="text"
                id="fullName"
                name="fullName"
                value={formData.fullName}
                onChange={handleChange}
                placeholder="Họ và tên"
                required
              />
              <i className="fas fa-user"></i>
            </div>

            <div className="form-group">
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                placeholder="Email"
                required
              />
              <i className="fas fa-envelope"></i>
            </div>

            <div className="form-group">
              <input
                type="tel"
                id="phone"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                placeholder="Số điện thoại"
              />
              <label>Số điện thoại</label>
            </div>

            <div className="input-group">
              <textarea
                name="message"
                value={formData.message}
                onChange={handleChange}
                required
                rows="4"
              ></textarea>
              <label>Nội dung *</label>
            </div>

            <button type="submit">
              Gửi tin nhắn
              <i className="fas fa-arrow-right"></i>
            </button>
          </form>

          {notification.show && (
            <div className={`alert ${notification.type}`}>
              {notification.message}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Contact;
