import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Navbar.css';
import { FaBars, FaTimes } from 'react-icons/fa';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const location = useLocation();

  // Xử lý scroll
  useEffect(() => {
    const handleScroll = () => {
      const offset = window.scrollY;
      if (offset > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Đóng menu khi chuyển trang
  useEffect(() => {
    setIsOpen(false);
  }, [location]);

  return (
    <nav className={`navbar ${scrolled ? 'scrolled' : ''}`}>
      <div className="navbar-container">
        <Link to="/" className="navbar-logo">
          TechKids
        </Link>

        <div className="menu-icon" onClick={() => setIsOpen(!isOpen)}>
          {isOpen ? <FaTimes /> : <FaBars />}
        </div>

        <ul className={`nav-menu ${isOpen ? 'active' : ''}`}>
          <li className="nav-item">
            <Link 
              to="/" 
              className={`nav-link ${location.pathname === '/' ? 'active' : ''}`}
            >
              Trang chủ
            </Link>
          </li>

          <li className="nav-item dropdown">
            <Link 
              to="/about" 
              className={`nav-link ${location.pathname.includes('/about') ? 'active' : ''}`}
            >
              Giới thiệu
            </Link>
            <ul className="dropdown-menu">
              <li><Link to="/about/mission">Sứ mệnh</Link></li>
              <li><Link to="/about/vision">Tầm nhìn</Link></li>
              <li><Link to="/about/team">Đội ngũ</Link></li>
              <li><Link to="/about/history">Lịch sử</Link></li>
            </ul>
          </li>

          <li className="nav-item dropdown">
            <Link 
              to="/courses" 
              className={`nav-link ${location.pathname.includes('/courses') ? 'active' : ''}`}
            >
              Khóa học
            </Link>
            <ul className="dropdown-menu">
              <li><Link to="/courses/all">Tất cả khóa học</Link></li>
              <li><Link to="/courses/curriculum">Chương trình học</Link></li>
              <li><Link to="/courses/register">Đăng ký học</Link></li>
            </ul>
          </li>

          <li className="nav-item dropdown">
            <Link 
              to="/programs" 
              className={`nav-link ${location.pathname.includes('/programs') ? 'active' : ''}`}
            >
              Chương trình
            </Link>
            <ul className="dropdown-menu">
              <li><Link to="/programs/special">Chương trình đặc biệt</Link></li>
              <li><Link to="/programs/events">Sự kiện</Link></li>
            </ul>
          </li>

          <li className="nav-item dropdown">
            <Link 
              to="/resources" 
              className={`nav-link ${location.pathname.includes('/resources') ? 'active' : ''}`}
            >
              Tài nguyên
            </Link>
            <ul className="dropdown-menu">
              <li><Link to="/resources/materials">Tài liệu học tập</Link></li>
              <li><Link to="/resources/guides">Hướng dẫn</Link></li>
              <li><Link to="/resources/links">Liên kết hữu ích</Link></li>
            </ul>
          </li>

          <li className="nav-item">
            <Link 
              to="/contact" 
              className={`nav-link ${location.pathname === '/contact' ? 'active' : ''}`}
            >
              Liên hệ
            </Link>
          </li>
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
