// Import cần thiết
import React, { useState } from 'react';
// useState: Cần thiết để quản lý state trong functional component

// Dữ liệu tĩnh - Tách riêng để dễ quản lý và bảo trì
const CONTACT_INFO = [
  {
    icon: 'map-marker-alt',    // Icon từ Font Awesome
    title: 'Địa Chỉ',         // Tiêu đề hiển thị
    content: 'Ở đâu còn lâu mới nói'  // Nội dung
  },
  // ... các thông tin khác
];

// State Management
const initialFormState = {
  fullName: '',    // Khởi tạo rỗng cho form
  email: '',       // Giúp reset form dễ dàng
  phone: '',       // và tái sử dụng giá trị khởi tạo
  message: ''
};

const [formData, setFormData] = useState(initialFormState);
// Sử dụng useState để theo dõi và cập nhật dữ liệu form

// Handler Functions
const handleChange = (e) => {
  const { name, value } = e.target;
  // Destructuring để lấy name và value từ event
  setFormData(prev => ({ ...prev, [name]: value }));
  // Spread operator (...) giữ nguyên các giá trị cũ
  // [name]: value cập nhật giá trị mới dựa trên name
};

// Component con InfoItem
const InfoItem = ({ icon, title, content }) => (
  // Props destructuring để code gọn gàng
  <div className="info-item">
    {/* Cấu trúc HTML cho mỗi item thông tin */}
  </div>
);
export default Contact;
