import React, { useState } from 'react';
import './CourseProgram.css';

const CourseProgram = () => {
  // State để quản lý tab đang active
  const [activeTab, setActiveTab] = useState('intro');

  // Dữ liệu chương trình học
  const programData = {
    intro: {
      title: "Giới thiệu chung",
      content: {
        description: "Khóa học lập trình dành cho trẻ em từ 5-16 tuổi, giúp phát triển tư duy logic và kỹ năng giải quyết vấn đề.",
        targetAudience: [
          "Trẻ em từ 5-7 tuổi: Khóa Scratch Jr",
          "Trẻ em từ 8-12 tuổi: Khóa Python cơ bản",
          "Trẻ em từ 13-16 tuổi: Khóa lập trình Web"
        ],
        benefits: [
          "Phát triển tư duy logic và sáng tạo",
          "Rèn luyện kỹ năng giải quyết vấn đề",
          "Tạo nền tảng cho tương lai số",
          "Học thông qua các dự án thực tế thú vị"
        ]
      }
    },
    content: {
      title: "Nội dung khóa học",
      content: {
        chapters: [
          {
            title: "Phần 1: Làm quen với lập trình",
            lessons: ["Tư duy lập trình", "Giới thiệu công cụ"]
          },
          {
            title: "Phần 2: Thực hành cơ bản",
            lessons: ["Biến và kiểu dữ liệu", "Cấu trúc điều khiển"]
          }
        ],
        format: [
          "Video bài giảng",
          "Bài tập thực hành",
          "Project cuối khóa",
          "Kiểm tra đánh giá"
        ],
        schedule: "8-16 tuần (2 buổi/tuần, 2 giờ/buổi)"
      }
    },
    results: {
      title: "Kết quả đạt được",
      content: {
        skills: [
          "Nắm vững kiến thức nền tảng về lập trình",
          "Có khả năng tự học và phát triển",
          "Hoàn thành các project thực tế",
          "Tư duy giải quyết vấn đề"
        ],
        certificate: "Chứng chỉ hoàn thành khóa học",
        applications: [
          "Tạo game và ứng dụng đơn giản",
          "Xây dựng website cơ bản",
          "Áp dụng vào học tập và cuộc sống"
        ]
      }
    },
    instructor: {
      title: "Giảng viên",
      content: {
        name: "ThS. Nguyễn Văn A",
        experience: "10 năm kinh nghiệm giảng dạy",
        achievements: [
          "Thạc sĩ Công nghệ thông tin",
          "Chứng chỉ Google Certified Educator",
          "Đào tạo hơn 1000 học viên"
        ]
      }
    },
    pricing: {
      title: "Học phí & Ưu đãi",
      content: {
        price: "6,000,000 VNĐ",
        discount: "Giảm 20% khi đăng ký sớm",
        policies: [
          "Học thử miễn phí 1 buổi",
          "Hoàn tiền trong 7 ngày đầu",
          "Hỗ trợ trả góp 0%"
        ]
      }
    }
  };

  return (
    <div className="course-program">
      <div className="container">
        <h1 className="program-title">Chương Trình Đào Tạo</h1>
        
        {/* Tab Navigation */}
        <div className="program-tabs">
          <button 
            className={`tab-btn ${activeTab === 'intro' ? 'active' : ''}`}
            onClick={() => setActiveTab('intro')}
          >
            Giới thiệu
          </button>
          <button 
            className={`tab-btn ${activeTab === 'content' ? 'active' : ''}`}
            onClick={() => setActiveTab('content')}
          >
            Nội dung
          </button>
          <button 
            className={`tab-btn ${activeTab === 'results' ? 'active' : ''}`}
            onClick={() => setActiveTab('results')}
          >
            Kết quả
          </button>
          <button 
            className={`tab-btn ${activeTab === 'instructor' ? 'active' : ''}`}
            onClick={() => setActiveTab('instructor')}
          >
            Giảng viên
          </button>
          <button 
            className={`tab-btn ${activeTab === 'pricing' ? 'active' : ''}`}
            onClick={() => setActiveTab('pricing')}
          >
            Học phí
          </button>
        </div>

        {/* Tab Content */}
        <div className="tab-content">
          {activeTab === 'intro' && (
            <div className="content-section">
              <h2>{programData.intro.title}</h2>
              <p>{programData.intro.content.description}</p>
              <h3>Đối tượng phù hợp:</h3>
              <ul>
                {programData.intro.content.targetAudience.map((item, index) => (
                  <li key={index}>{item}</li>
                ))}
              </ul>
              <h3>Lợi ích:</h3>
              <ul>
                {programData.intro.content.benefits.map((item, index) => (
                  <li key={index}>{item}</li>
                ))}
              </ul>
            </div>
          )}

          {activeTab === 'content' && (
            <div className="content-section">
              <h2>{programData.content.title}</h2>
              <h3>Chương trình học:</h3>
              {programData.content.content.chapters.map((chapter, index) => (
                <div key={index} className="chapter">
                  <h4>{chapter.title}</h4>
                  <ul>
                    {chapter.lessons.map((lesson, i) => (
                      <li key={i}>{lesson}</li>
                    ))}
                  </ul>
                </div>
              ))}
              <h3>Hình thức học:</h3>
              <ul>
                {programData.content.content.format.map((item, index) => (
                  <li key={index}>{item}</li>
                ))}
              </ul>
              <p><strong>Lộ trình:</strong> {programData.content.content.schedule}</p>
            </div>
          )}

          {/* Các tab khác tương tự */}
        </div>
      </div>
    </div>
  );
};

export default CourseProgram;
