import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';

// Import các components
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import Home from './components/Home';
import Courses from './components/Courses';
import Schedule from './components/Schedule';
import Contact from './components/Contact';

function App() {
  return (
    <Router>
      <div className="app">
        <Navbar />
        
        <Routes>
          {/* Trang chủ */}
          <Route path="/" element={<Home />} />
          
          {/* Trang Khóa học */}
          <Route path="/khoa-hoc" element={<Courses />} />
          
          {/* Trang Lịch học */}
          <Route path="/lich-hoc" element={<Schedule />} />
          
          {/* Trang Liên hệ */}
          <Route path="/lien-he" element={<Contact />} />
        </Routes>

        <Footer />
      </div>
    </Router>
  );
}

export default App;

/*
Giải thích cấu trúc:

1. Routing:
   - BrowserRouter bao bọc toàn bộ ứng dụng
   - Routes định nghĩa các đường dẫn
   - Route cho từng trang cụ thể

2. Layout:
   - Navbar và Footer xuất hiện ở tất cả các trang
   - Nội dung thay đổi theo route

3. Components:
   - Tái sử dụng components cho các trang khác nhau
   - Có thể thêm các trang mới dễ dàng

4. UX:
   - Navigation mượt mà giữa các trang
   - Không reload trang khi chuyển route
   - Giữ trạng thái của components
*/
